// src/App.js
import React, { useState } from 'react';
import PredictionForm from './components/PredictionForm';
import ResultDisplay from './components/ResultDisplay';
import './App.css';

function App() {
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handlePrediction = async (formData) => {
    setLoading(true);
    setError(null);
    
    try {
      // In a real implementation, this would call your API
      // For now, we'll simulate a response
      const response = await fetch('https://your-flask-app.onrender.com/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      
      if (!response.ok) {
        throw new Error('Prediction failed');
      }
      
      const data = await response.json();
      setPrediction(data.prediction);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Student Performance Predictor</h1>
        <p>Predict math scores based on student characteristics</p>
      </header>
      
      <main className="App-main">
        <PredictionForm onSubmit={handlePrediction} />
        
        {loading && <div className="loading">Processing your prediction...</div>}
        
        {error && (
          <div className="error">
            Error: {error}
          </div>
        )}
        
        {prediction !== null && !loading && (
          <ResultDisplay prediction={prediction} />
        )}
      </main>
      
      <footer className="App-footer">
        <p>ML App powered by React and Flask</p>
      </footer>
    </div>
  );
}

export default App;
