// src/App.js
import React, { useState } from 'react';
import PredictionForm from './components/PredictionForm';
import ResultDisplay from './components/ResultDisplay';
import { predictMathScore, checkHealth } from './services/api';
import './App.css';

function App() {
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');

  // Check backend health on component mount
  React.useEffect(() => {
    const checkBackend = async () => {
      try {
        const isHealthy = await checkHealth();
        setBackendStatus(isHealthy ? 'healthy' : 'unhealthy');
      } catch (err) {
        setBackendStatus('unhealthy');
      }
    };
    
    checkBackend();
  }, []);

  const handlePrediction = async (formData) => {
    setLoading(true);
    setError(null);
    
    try {
      const response = await predictMathScore(formData);
      setPrediction(response.prediction);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Student Performance Predictor</h1>
        <p>Predict math scores based on student characteristics</p>
        <div className={`backend-status ${backendStatus}`}>
          Backend Status: {backendStatus === 'healthy' ? '✅ Connected' : '❌ Disconnected'}
        </div>
      </header>
      
      <main className="App-main">
        <PredictionForm onSubmit={handlePrediction} />
        
        {loading && <div className="loading">Processing your prediction...</div>}
        
        {error && (
          <div className="error">
            <h3>Error</h3>
            <p>{error}</p>
            <p>Please check if your backend is running on Render.</p>
          </div>
        )}
        
        {prediction !== null && !loading && (
          <ResultDisplay prediction={prediction} />
        )}
      </main>
      
      <footer className="App-footer">
        <p>ML App powered by React and Flask</p>
      </footer>
    </div>
  );
}

export default App;
